<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="line"><span class="token keyword">CREATE</span> <span class="token operator">OR</span> <span class="token keyword">REPLACE</span> <span class="token keyword">PROCEDURE</span> p1<span class="token punctuation">(</span></span>
<span class="line">	community_id	<span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">,</span></span>
<span class="line">    student_id	<span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">)</span></span>
<span class="line"><span class="token punctuation">)</span></span>
<span class="line"><span class="token keyword">AS</span> </span>
<span class="line"><span class="token keyword">DECLARE</span></span>
<span class="line"> 	SQL_STR <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"> 	join_date <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">32</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token keyword">BEGIN</span></span>
<span class="line">	SQL_STR :<span class="token operator">=</span> <span class="token string">'SELECT 加入时间 FROM 参加社团表 WHERE 社团号 = $1 AND 学号 = $2;'</span><span class="token punctuation">;</span></span>
<span class="line">    <span class="token keyword">EXECUTE</span> IMMEDIATE SQL_STR <span class="token keyword">INTO</span> join_date <span class="token keyword">USING</span> community_id<span class="token punctuation">,</span>student_id<span class="token punctuation">;</span></span>
<span class="line">	RAISE NOTICE <span class="token string">'加入时间: %'</span><span class="token punctuation">,</span> join_date<span class="token punctuation">;</span></span>
