<div class="language-sql line-numbers-mode" data-ext="sql" data-title="sql"><pre v-pre class="language-sql"><code><span class="line"><span class="token keyword">CREATE</span> <span class="token operator">OR</span> <span class="token keyword">REPLACE</span> <span class="token keyword">PROCEDURE</span> p4<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
<span class="line"><span class="token keyword">AS</span> </span>
<span class="line"><span class="token keyword">DECLARE</span></span>
<span class="line">    <span class="token keyword">TYPE</span> CURSOR_TYPE <span class="token operator">IS</span> REF <span class="token keyword">CURSOR</span><span class="token punctuation">;</span>	<span class="token comment">--定义ref游标类型CURSOR_TYPE</span></span>
<span class="line">    C CURSOR_TYPE<span class="token punctuation">;</span></span>
<span class="line"> 	SQL_STR <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"> 	community_id <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"> 	community_name <span class="token keyword">VARCHAR</span><span class="token punctuation">(</span><span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token keyword">BEGIN</span></span>
<span class="line">	SQL_STR :<span class="token operator">=</span> <span class="token string">'SELECT 社团号,名称 FROM 社团表;'</span><span class="token punctuation">;</span></span>
<span class="line">    </span>
<span class="line">    <span class="token keyword">OPEN</span> C <span class="token keyword">FOR</span> SQL_STR<span class="token punctuation">;</span>		<span class="token comment">--绑定游标</span></span>
<span class="line">    <span class="token keyword">LOOP</span>									<span class="token comment">--开启循环</span></span>
<span class="line">        <span class="token keyword">FETCH</span> C <span class="token keyword">INTO</span> community_id<span class="token punctuation">,</span>community_name<span class="token punctuation">;</span>            <span class="token comment">--将结果赋值给变量join_date</span></span>
<span class="line">        <span class="token keyword">EXIT</span> <span class="token keyword">WHEN</span> C<span class="token operator">%</span>NOTFOUND<span class="token punctuation">;</span>			<span class="token comment">--如果查询结果为空则退出</span></span>
<span class="line">        RAISE NOTICE <span class="token string">'社团号: %,名称: %'</span><span class="token punctuation">,</span> community_id<span class="token punctuation">,</span>community_name<span class="token punctuation">;</span>		<span class="token comment">--将结果输入到屏幕上</span></span>
<span class="line">    <span class="token keyword">END</span> <span class="token keyword">LOOP</span><span class="token punctuation">;</span>								<span class="token comment">--结束循环</span></span>
<span class="line">    <span class="token keyword">CLOSE</span> C<span class="token punctuation">;</span></span>
<span class="line"><span class="token keyword">END</span><span class="token punctuation">;</span></span>
<span class="line"><span class="token operator">/</span></span>
<span class="line"></span>
<span class="line"><span class="token keyword">CALL</span> p4<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span></code></pre>
